public class _5325_包含所有三种字符的子字符串数目 {
	public static void main(String[] args) {
		String str = "";
		System.out.println(numberOfSubstrings(str));
	}
	public static int numberOfSubstrings(String s) {
		if(s.length()<3){
			return 0;
		}
		int res = 0;
		char[] ch = abc(s);
		if(ch[0] == 'd'){
			return 0;
		}
		return (s.length()-s.indexOf(ch[2],s.indexOf(ch[1])))+numberOfSubstrings(s.substring(s.indexOf(ch[0])+1));
	}
	public static char[] abc(String s){
		char[] ch = new char[3];
		int[] aaa = new int[3];
		char a = 'a';
		for(int i = 0;i< aaa.length;i++){
			aaa[i] = s.indexOf((char)(a));
			a++;
		}
		Arrays.sort(aaa);
		if(aaa[0] == -1){
			ch[0] = 'd';
			ch[1] = 'd';
			ch[2] = 'd';
			return ch;
		}
		for (int i = 0; i < 3; i++) {
			ch[i] = s.charAt(aaa[i]);
		}
		return ch;
	}
}
